
document.getElementById("btnAdicionar").addEventListener('click', function (e) {

    e.preventDefault();

    var notificacao = document.getElementsByClassName("notificacao");
    notificacao[0].classList.remove("is-invisible");

    var novaTarefa = document.getElementById("txt").value;
    var corpoTabela = document.getElementById("corpoTabela");

    var tr = document.createElement('tr');
    var td = tr.appendChild(document.createElement('td'));
    var td2 = tr.appendChild(document.createElement('td'));
    var td3 = tr.appendChild(document.createElement('td'));
    td.innerHTML = novaTarefa;
    td2.innerHTML = "<span class='tag is-info'>Incompleta</span>";
    td3.innerHTML = "<a class='button is-danger is-small' onclick='Excluir(this)'>Excluir</a>";
    corpoTabela.appendChild(tr);

    var atividade = document.getElementById("atividade");
    atividade.innerHTML = document.getElementById("txt").value;
    document.getElementById("txt").value = "";

    var acao = document.getElementById("acao");
    acao.innerHTML = "incluída.";

});


function Excluir(btn) {

    var notificacao = document.getElementsByClassName("notificacao");
    notificacao[0].classList.remove("is-invisible");
    
    var acao = document.getElementById("acao");
    acao.innerHTML = "excluída.";
    
    var row = btn.parentNode.parentNode;
    row.parentNode.removeChild(row);
}

function Esconder (){
    var notificacao = document.getElementsByClassName("notificacao");    
    notificacao[0].classList.add("is-invisible");
}